Definition getPartition (m: TTModel) (rows: list Row) (port: Port) : (*zeroPart*) (list Row) * (* onePart *) (list Row) :=

  (* Select the rows for which the port is false *)
  let _zeroPart : list Row :=
      filter
        (fun r =>
           match Row_getCells r m with
           | None => false
           | Some cs =>
             existsb
               (fun c =>
                  match Cell_getPort c m with
                  | None => false 
                  | Some p => 
                    andb
                      (beq_Port p port)
                      (eqb (Cell_getValue c) false)
                  end)
               cs
           end)
        rows in

  let _onePart : list Row :=
      filter
        (fun r =>
           match Row_getCells r m with
           | None => false
           | Some cs =>
             existsb
               (fun c =>
                  match Cell_getPort c m with
                  | None => false 
                  | Some p => 
                    andb
                      (beq_Port p port)
                      (eqb (Cell_getValue c) true)
                  end)
               cs
           end)
        rows in

  (_zeroPart, _onePart).

Definition Port_dec: forall x y : Port, {x = y} + {x <> y}.
Proof.
  decide equality.
  + apply string_dec.
  + apply string_dec.
  + decide equality; apply string_dec.
Qed.

Inductive BTree : Type :=
| leaf : option Cell -> option Row -> option Row -> BTree
| node : option Cell -> BTree -> BTree -> BTree.

Program Fixpoint getTree (m: TTModel) (rows: list Row) (usablePorts : list Port) {measure (length usablePorts)}: BTree
  :=
    let _port : option Port :=
        find
          (fun p =>
             forallb
               (fun r =>
                  match Row_getCells r m with
                  | None => false
                  | Some cs => 
                    existsb
                      (* it does not return an error when a cell has no port *)
                      (fun p1 : option Port =>
                         match p1 with
                         | None => false
                         | Some p2 => beq_Port p p2
                         end)
                      (map
                         (fun c : Cell => Cell_getPort c m)
                         cs)
                  end)
               rows )
          usablePorts in
    let _cell : option Cell :=
        match hd_error rows with
        | None => None
        | Some r => 
          match (Row_getCells r m) with
          | None => None
          | Some cs =>
            find (fun c =>
                    match Cell_getPort c m with
                    | None => false
                    | Some p =>
                      match _port with
                      | None => false
                      | Some p1 => 
                        beq_Port p p1
                      end
                    end
                 ) cs
          end
        end in
    let _part : (list Row) * (list Row) :=
        match _port with
        | None => (nil, nil)
        | Some port1 => 
          getPartition m rows port1
        end in
    let _updatedPorts : list Port :=
        match _port with
        | None => nil
        | Some port1 =>
          remove Port_dec port1 usablePorts
        end
    in
    if ( beq_nat (length (fst _part)) 1 ) then
      leaf _cell (hd_error (fst _part)) (hd_error (snd _part))   
    else
      match _updatedPorts with
      | nil => leaf _cell (hd_error (fst _part)) (hd_error (snd _part))
      | _ => 
      node _cell
           (getTree m (fst _part) _updatedPorts)
           (getTree m (snd _part) _updatedPorts)
      end.
